import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import axios from 'axios';

const Relatorios = () => {
  const [atividades, setAtividades] = useState([]);
  const [metas, setMetas] = useState([]);
  const userId = '67166256a7b695a9778513a2'; // ID fixo do usuário

  // Função para buscar atividades e metas do usuário
  useEffect(() => {
    const fetchAtividadesEMetas = async () => {
      try {
        // Fetch de atividades
        const atividadesResponse = await axios.get('http://localhost:3000/api/atividades');
        setAtividades(atividadesResponse.data);

        // Fetch de metas do usuário com o userId fixo
        const metasResponse = await axios.get(`http://localhost:3000/api/metas/user/${userId}`);
        setMetas(metasResponse.data);
      } catch (error) {
        console.error('Erro ao buscar atividades e metas:', error);
      }
    };

    fetchAtividadesEMetas();
  }, []);

  // Função para agrupar as atividades por tipo
  const agruparAtividadesPorTipo = () => {
    return atividades.reduce((acc, atividade) => {
      const { tipoAtividade, distancia } = atividade;
      if (!acc[tipoAtividade]) {
        acc[tipoAtividade] = 0;
      }
      acc[tipoAtividade] += distancia; // Soma a distância percorrida
      return acc;
    }, {});
  };

  // Função para calcular o progresso das metas
  const calcularProgresso = () => {
    const atividadesAgrupadas = agruparAtividadesPorTipo();
    return metas.map((meta) => {
      const distanciaPercorrida = atividadesAgrupadas[meta.tipo_atividade] || 0; // Se não houver atividade, distância é 0
      const progresso = {
        tipoAtividade: meta.tipo_atividade,
        valorMeta: meta.valor_meta,
        distanciaPercorrida: distanciaPercorrida,
        distanciaRestante: Math.max(meta.valor_meta - distanciaPercorrida, 0), // Garante que não fique negativo
      };
      return progresso;
    });
  };

  const progressoMetas = calcularProgresso();

  return (
    <div>
      <h2 className="text-center mt-4">Relatórios</h2>

      {/* Seção de Atividades Registradas */}
      <div>
        <h3 className="text-center mt-4">Atividades Registradas</h3>
        {atividades.length > 0 ? (
          <ul>
            {atividades.map((atividade, index) => (
              <li key={index}>
                <strong>Tipo:</strong> {atividade.tipoAtividade} <br />
                <strong>Distância:</strong> {atividade.distancia} km <br />
                <strong>Data:</strong> {atividade.data}
              </li>
            ))}
          </ul>
        ) : (
          <p>Nenhuma atividade registrada encontrada.</p>
        )}
      </div>

      {/* Seção de Metas Registradas */}
      <div>
        <h3 className="text-center mt-4">Metas Registradas</h3>
        {metas.length > 0 ? (
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Tipo de Atividade</th>
                <th>Valor da Meta</th>
                <th>Data Inicial</th>
                <th>Data Final</th>
                <th>Status da Meta</th>
              </tr>
            </thead>
            <tbody>
              {metas.map((meta, index) => (
                <tr key={index}>
                  <td>{meta.tipo_atividade}</td>
                  <td>{meta.valor_meta} km</td>
                  <td>{new Date(meta.data_inicial).toLocaleDateString()}</td>
                  <td>{new Date(meta.data_final).toLocaleDateString()}</td>
                  <td>{meta.status_meta}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        ) : (
          <p>Nenhuma meta registrada encontrada.</p>
        )}
      </div>

      {/* Seção de Progresso das Metas */}
      <div>
        <h3 className="text-center mt-4">Progresso das Metas</h3>
        {progressoMetas.length > 0 ? (
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Tipo de Atividade</th>
                <th>Distância Percorrida</th>
                <th>Distância Restante</th>
                <th>Valor da Meta</th>
              </tr>
            </thead>
            <tbody>
              {progressoMetas.map((progresso, index) => (
                <tr key={index}>
                  <td>{progresso.tipoAtividade}</td>
                  <td>{progresso.distanciaPercorrida} km</td>
                  <td>{progresso.distanciaRestante} km</td>
                  <td>{progresso.valorMeta} km</td>
                </tr>
              ))}
            </tbody>
          </Table>
        ) : (
          <p>Nenhum progresso de meta encontrado.</p>
        )}
      </div>
    </div>
  );
};

export default Relatorios;
