import React, { useEffect, useState } from 'react';
import { Table, Button } from 'react-bootstrap';
import axios from 'axios';

const Atividades = () => {
  const [atividades, setAtividades] = useState([]);
  const userId = '67166256a7b695a9778513a2'; // ID fixo do usuário

  useEffect(() => {
    const fetchAtividades = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/atividades');
        setAtividades(response.data);
      } catch (error) {
        console.error('Erro ao buscar atividades:', error);
      }
    };

    fetchAtividades();
  }, []);

  const excluirAtividade = async (atividadeId) => {
    try {
      await axios.delete(`http://localhost:3000/api/atividades/${atividadeId}`);
      setAtividades(atividades.filter((atividade) => atividade._id !== atividadeId)); // Remove a atividade excluída da lista
    } catch (error) {
      console.error('Erro ao excluir atividade:', error);
    }
  };

  return (
    <div>
      <h2 className="text-center mt-4">Atividades Registradas</h2>
      {atividades.length > 0 ? (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Tipo de Atividade</th>
              <th>Distância</th>
              <th>Data</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {atividades.map((atividade, index) => (
              <tr key={index}>
                <td>{atividade.tipoAtividade}</td>
                <td>{atividade.distancia} km</td>
                <td>{atividade.data}</td>
                <td>
                  <Button variant="danger" onClick={() => excluirAtividade(atividade._id)}>
                    Excluir
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      ) : (
        <p>Nenhuma atividade registrada encontrada.</p>
      )}
    </div>
  );
};

export default Atividades;
