import React, { useState, useEffect } from 'react';
import { Form, Button, ListGroup } from 'react-bootstrap';
import axios from 'axios';

const Metas = () => {
  const [meta, setMeta] = useState({
    // Removido o campo id_usuario do estado
    tipo_atividade: '',
    valor_meta: '',
    data_inicial: '',
    data_final: '',
    status_meta: 'ativo', // Definido como 'ativo' por padrão
  });

  const [metas, setMetas] = useState([]);

  // Função para buscar metas da API
  const fetchMetas = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/metas'); // Substitua pela URL da sua API
      setMetas(response.data);
    } catch (error) {
      console.error('Erro ao buscar metas:', error);
    }
  };

  useEffect(() => {
    fetchMetas();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMeta((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Adiciona o id_usuario fixo aqui antes de enviar
      const idUsuarioFixo = '67166256a7b695a9778513a2'; 
      const novaMeta = { ...meta, id_usuario: idUsuarioFixo }; // Adiciona o id_usuario fixo

      // Enviar dados para a API para registrar a nova meta
      await axios.post('http://localhost:3000/api/metas', novaMeta); // Substitua pela URL da sua API
      setMetas([...metas, novaMeta]); // Adiciona a nova meta ao estado local
      setMeta({ tipo_atividade: '', valor_meta: '', data_inicial: '', data_final: '', status_meta: 'ativo' }); // Limpa o formulário e define status_meta como 'ativo'
    } catch (error) {
      console.error('Erro ao registrar meta:', error);
    }
  };

  return (
    <div>
      <h2>Registrar Meta</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Tipo de Atividade</Form.Label>
          <Form.Control 
            type="text" 
            name="tipo_atividade" 
            value={meta.tipo_atividade} 
            onChange={handleChange} 
            placeholder="Digite o tipo de atividade" 
            required 
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Valor da Meta</Form.Label>
          <Form.Control 
            type="number" 
            name="valor_meta" 
            value={meta.valor_meta} 
            onChange={handleChange} 
            required 
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Data Inicial</Form.Label>
          <Form.Control 
            type="date" 
            name="data_inicial" 
            value={meta.data_inicial} 
            onChange={handleChange} 
            required 
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Data Final</Form.Label>
          <Form.Control 
            type="date" 
            name="data_final" 
            value={meta.data_final} 
            onChange={handleChange} 
            required 
          />
        </Form.Group>
        {/* O campo status_meta foi removido do formulário */}
        <Button type="submit">Adicionar</Button>
      </Form>

      <ListGroup className="mt-3">
        {metas.map((item, index) => (
          <ListGroup.Item key={index}>
            {`Atividade: ${item.tipo_atividade} - Meta: ${item.valor_meta} - De: ${item.data_inicial} até: ${item.data_final} - Status: ${item.status_meta}`}
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
};

export default Metas;
