// src/components/Atividades.js
import React, { useState, useEffect } from 'react';
import { Table, Button, Form } from 'react-bootstrap';
import axios from 'axios';

function Atividades() {
  const [atividades, setAtividades] = useState([]);
  const [tipoAtividade, setTipoAtividade] = useState('');
  const [dataAtividade, setDataAtividade] = useState('');
  const [duracao, setDuracao] = useState('');
  const [distancia, setDistancia] = useState('');
  const [intensidade, setIntensidade] = useState('');
  const [caloriasQueimadas, setCaloriasQueimadas] = useState('');
  const [observacoes, setObservacoes] = useState('');

  useEffect(() => {
    fetchAtividades();
  }, []);

  const fetchAtividades = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/atividades'); // Substitua pela sua URL da API
      console.log(response);
      setAtividades(response.data);
    } catch (error) {
      console.error('Erro ao buscar atividades:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const novaAtividade = {
      tipoAtividade,
      dataAtividade,
      duracao,
      distancia,
      intensidade,
      caloriasQueimadas,
      observacoes,
    };

    try {
      await axios.post('http://localhost:3000/api/atividades', novaAtividade); // Substitua pela sua URL da API
      fetchAtividades(); // Atualiza a lista de atividades
      // Limpa os campos do formulário
      setTipoAtividade('');
      setDataAtividade('');
      setDuracao('');
      setDistancia('');
      setIntensidade('');
      setCaloriasQueimadas('');
      setObservacoes('');
    } catch (error) {
      console.error('Erro ao registrar atividade:', error);
    }
  };

  const excluirAtividade = async (atividadeId) => {
    console.log(atividadeId)
    try {
      await axios.delete(`http://localhost:3000/api/atividades/${atividadeId}`); // Substitua pela sua URL da API
      setAtividades(atividades.filter((atividade) => atividade._id !== atividadeId)); // Remove a atividade excluída da lista
    } catch (error) {
      console.error('Erro ao excluir atividade:', error);
    }
  };

  return (
    <div>
      <h2 className="text-center mt-4">Registrar Atividade</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="tipoAtividade">
          <Form.Label>Tipo de Atividade</Form.Label>
          <Form.Control
            type="text"
            value={tipoAtividade}
            onChange={(e) => setTipoAtividade(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="dataAtividade">
          <Form.Label>Data da Atividade</Form.Label>
          <Form.Control
            type="date"
            value={dataAtividade}
            onChange={(e) => setDataAtividade(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="duracao">
          <Form.Label>Duração (minutos)</Form.Label>
          <Form.Control
            type="number"
            value={duracao}
            onChange={(e) => setDuracao(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="distancia">
          <Form.Label>Distância (km)</Form.Label>
          <Form.Control
            type="number"
            value={distancia}
            onChange={(e) => setDistancia(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="intensidade">
          <Form.Label>Intensidade</Form.Label>
          <Form.Control
            type="text"
            value={intensidade}
            onChange={(e) => setIntensidade(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="caloriasQueimadas">
          <Form.Label>Calorias Queimadas</Form.Label>
          <Form.Control
            type="number"
            value={caloriasQueimadas}
            onChange={(e) => setCaloriasQueimadas(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="observacoes">
          <Form.Label>Observações</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            value={observacoes}
            onChange={(e) => setObservacoes(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Registrar Atividade
        </Button>
      </Form>

      <h2 className="text-center mt-4">Atividades Registradas</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Tipo de Atividade</th>
            <th>Data da Atividade</th>
            <th>Duração (min)</th>
            <th>Distância (km)</th>
            <th>Intensidade</th>
            <th>Calorias Queimadas</th>
            <th>Observações</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {atividades.map((atividade) => (
            <tr key={atividade._id}>
              <td>{atividade.tipoAtividade}</td>
              <td>{new Date(atividade.dataAtividade).toLocaleDateString()}</td>
              <td>{atividade.duracao}</td>
              <td>{atividade.distancia}</td>
              <td>{atividade.intensidade}</td>
              <td>{atividade.caloriasQueimadas}</td>
              <td>{atividade.observacoes}</td>
              <td>
                <Button variant="danger" onClick={() => excluirAtividade(atividade._id)}>
                  Excluir
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Atividades;
