// src/components/RegistrarMeta.js
import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios';

function RegistrarMeta({ voltarParaMetas }) {
  const [tipoAtividade, setTipoAtividade] = useState('');
  const [valorMeta, setValorMeta] = useState('');
  const [dataInicial, setDataInicial] = useState('');
  const [dataFinal, setDataFinal] = useState('');
  const [statusMeta, setStatusMeta] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const novaMeta = {
      tipo_atividade: tipoAtividade,
      valor_meta: valorMeta,
      data_inicial: dataInicial,
      data_final: dataFinal,
      status_meta: statusMeta,
    };

    try {
      await axios.post('http://localhost:3000/api/metas', novaMeta); 
      voltarParaMetas(); // Volta para a tela de metas registradas após o registro
    } catch (error) {
      console.error('Erro ao registrar meta:', error);
    }
  };

  return (
    <div>
      <h2 className="text-center mt-4">Registrar Nova Meta</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="tipoAtividade">
          <Form.Label>Tipo de Atividade</Form.Label>
          <Form.Control
            type="text"
            value={tipoAtividade}
            onChange={(e) => setTipoAtividade(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="valorMeta">
          <Form.Label>Valor da Meta</Form.Label>
          <Form.Control
            type="number"
            value={valorMeta}
            onChange={(e) => setValorMeta(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="dataInicial">
          <Form.Label>Data Inicial</Form.Label>
          <Form.Control
            type="date"
            value={dataInicial}
            onChange={(e) => setDataInicial(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="dataFinal">
          <Form.Label>Data Final</Form.Label>
          <Form.Control
            type="date"
            value={dataFinal}
            onChange={(e) => setDataFinal(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group controlId="statusMeta">
          <Form.Label>Status da Meta</Form.Label>
          <Form.Control
            type="text"
            value={statusMeta}
            onChange={(e) => setStatusMeta(e.target.value)}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Registrar Meta
        </Button>
        <Button variant="secondary" onClick={voltarParaMetas} className="ml-2">
          Voltar
        </Button>
      </Form>
    </div>
  );
}

export default RegistrarMeta;
