import React, { useState, useEffect } from 'react';
import { Table, Button } from 'react-bootstrap';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Metas = () => {
  const [metas, setMetas] = useState([]);
  const navigate = useNavigate();

  const idUsuarioFixo = '67166256a7b695a9778513a2'; 

  useEffect(() => {
    fetchMetas();
  }, []);

  const fetchMetas = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/api/metas/user/${idUsuarioFixo}`); 
      setMetas(response.data);
    } catch (error) {
      console.error('Erro ao buscar metas:', error);
    }
  };

  const excluirMeta = async (metaId) => {
    try {
      await axios.delete(`http://localhost:3000/api/metas/${metaId}`); 
      setMetas(metas.filter((meta) => meta._id !== metaId)); 
    } catch (error) {
      console.error('Erro ao excluir meta:', error);
    }
  };

  const irParaRegistrarMeta = () => {
    navigate('/registrar-meta'); // Navegar para a página de registro de metas
  };

  return (
    <div>
      <h2 className="text-center mt-4">Metas Registradas</h2>
      <Button variant="primary" className="mb-3" onClick={irParaRegistrarMeta}>
        Registrar Nova Meta
      </Button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Tipo de Atividade</th>
            <th>Valor da Meta</th>
            <th>Data Inicial</th>
            <th>Data Final</th>
            <th>Status da Meta</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {metas.map((meta) => (
            <tr key={meta._id}>
              <td>{meta.tipo_atividade}</td>
              <td>{meta.valor_meta}</td>
              <td>{new Date(meta.data_inicial).toLocaleDateString()}</td>
              <td>{new Date(meta.data_final).toLocaleDateString()}</td>
              <td>{meta.status_meta}</td>
              <td>
                <Button variant="danger" onClick={() => excluirMeta(meta._id)}>
                  Excluir
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default Metas;
